sp_widget.client_script:
function($rootScope, $scope, spUtil) {
	/* widget controller */
	var c = this;

	spUtil.addInfoMessage("hello service portal");

	c.selectItem = function(index){
		var id = c.data.notes[index].sys_id;
		console.log("item no. clicked : " + id);

		$rootScope.noteID = id;
		$rootScope.$emit("selectNote", id);
	}

	$rootScope.$on("noteUpdated", function(event, data){
		c.server.refresh();
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	var gr = new GlideRecord("x_snc_createnotes_note");

	gr.addQuery("user", gs.getUserID());
	gr.orderByDesc("sys_created_on");
	gr.query();

	data.notes = [];

	while(gr.next()){
		var noteObj = {};

		//noteObj.number = gr.number.toString();
		//noteObj.title = gr.title.toString();
		//noteObj.sys_id = gr.sys_id.toString();
		$sp.getRecordDisplayValues(noteObj, gr, "number,title,sys_id");
		noteObj.note = gr.note ? gr.note.toString().slice(0, 20) : "";

		data.notes.push(noteObj);
	}

})();
